package com.starfish.kol.model.elements;

import com.starfish.kol.connection.Session;
import com.starfish.kol.model.elements.basic.BasicAction;
import com.starfish.kol.model.elements.interfaces.DeferredGameAction;
import com.starfish.kol.model.elements.interfaces.Multiuseable;
import com.starfish.kol.model.elements.interfaces.SubtextElement;

public class MultiusableElement implements Multiuseable {
	/**
	 * Autogenerated by eclipse.
	 */
	private static final long serialVersionUID = 1517694327159233372L;
	
	private final SubtextElement base;
	private final String uselink;
	private final Session session;
		
	public MultiusableElement(Session session, SubtextElement base, String uselink) {
		this.session = session;
		this.base = base;
		this.uselink = uselink;
	}
	
	@Override
	public String getText() {
		return base.getText();
	}

	@Override
	public String getImage() {
		return base.getImage();
	}

	@Override
	public String getSubtext() {
		return base.getSubtext();
	}
	
	@Override
	public DeferredGameAction use(String quantity) {
		return new BasicAction(session, uselink + quantity);
	}
}
