package com.starfish.kol.model.interfaces;

import com.starfish.kol.model.Model;
import com.starfish.kol.request.Request;
import com.starfish.kol.request.ResponseHandler;


public class ActionItem implements ModelItem, DeferredAction<Model<?>>
{
	/**
	 * Autogenerated by eclipse.
	 */
	private static final long serialVersionUID = 8834750738130793384L;
	
	private String text;
	private String action;
	private String img;
	
	public ActionItem(String text, String action) {
		this(text, "", action);
	}

	public ActionItem(String text, String img, String action) {
		this.text = text;
		this.img = img;
		this.action = action;
	}
	
	@Override
	public String getText() {
		return this.text;
	}

	@Override
	public String getImage() {
		return this.img;
	}
	
	public void select(Model<?> context) {
		context.makeRequest(new Request(this.action, ResponseHandler.none));
	}
	
	@Override
	public String toString() {
		return this.text;
	}

	@Override
	public void submit(Model<?> context) {
		this.select(context);
	}
}
