package com.starfish.kol.model.models.skill;

import com.starfish.kol.connection.ServerReply;
import com.starfish.kol.connection.Session;
import com.starfish.kol.model.GroupModel;
import com.starfish.kol.model.models.WebModel;

public class SkillsModel extends GroupModel<SkillsSubmodel> {
	/**
	 * Autogenerated by eclipse.
	 */
	private static final long serialVersionUID = 295714863597L;

	private WebModel resultsPane;

	private final SkillsListModel skills;
	private final ItemsListModel items;
	private final boolean usedItem;

	public SkillsModel(Session s, ServerReply text) {
		super(s);

		this.resultsPane = extractResultsPane(getSession(), text);
		this.skills = new SkillsListModel(s, text);
		this.items = new ItemsListModel(s, text);

		usedItem = text.url.contains("useditem");
	}

	public WebModel getResultsPane() {
		return resultsPane;
	}

	public boolean getJustUsedItem() {
		return usedItem;
	}

	@Override
	public int getActiveChild() {
		return 0;
	}

	@Override
	public SkillsSubmodel[] getChildren() {
		return new SkillsSubmodel[]{skills, items};
	}
}
