package com.starfish.kol.android.binders;

import java.io.Serializable;

import android.text.Html;
import android.view.View;
import android.widget.TextView;

import com.starfish.kol.android.R;
import com.starfish.kol.model.models.chat.ChatText;

public class ChatBinder implements Binder<ChatText>, Serializable {
	/**
	 * Autogenerated by eclipse.
	 */
	private static final long serialVersionUID = 3303232279575856149L;
	
	public static final ChatBinder ONLY = new ChatBinder();
	
	private ChatBinder() {
		
	}
	
	@Override
	public int getView() {
		return R.layout.list_chat_item;
	}
	
	@Override
	public void bind(View view, ChatText model) {
		TextView text = (TextView)view.findViewById(R.id.list_chat_text);
		text.setText(Html.fromHtml(model.getText()));
	}

}
