package com.starfish.kol.android.binders;

import java.io.Serializable;

import android.text.Html;
import android.view.View;
import android.widget.ImageView;
import android.widget.TextView;

import com.starfish.kol.android.R;
import com.starfish.kol.android.util.ImageDownloader;
import com.starfish.kol.model.elements.interfaces.ModelElement;

public class ElementBinder implements Binder<ModelElement>, Serializable {
	/**
	 * Autogenerated by eclipse.
	 */
	private static final long serialVersionUID = -8225060810659961491L;
	
	public final static ElementBinder ONLY = new ElementBinder();
	
	private ElementBinder() {
		
	}
	
	@Override
	public int getView() {
		return R.layout.list_generic_item;
	}

	@Override
	public void bind(View view, ModelElement model) {
		TextView text = (TextView)view.findViewById(R.id.list_item_text);
		text.setText(Html.fromHtml(model.getText()));
		
		ImageView img = (ImageView)view.findViewById(R.id.list_item_image);
		if(model.getImage() == null || model.getImage().length() == 0) {
			img.setVisibility(View.GONE);
		} else {
			img.setVisibility(View.VISIBLE);
			ImageDownloader.loadFromUrl(img, model.getImage());
		}
	}

}
