package com.starfish.kol.android.binders;

import java.io.Serializable;

import android.graphics.Color;
import android.view.View;
import android.widget.TextView;

import com.starfish.kol.android.R;
import com.starfish.kol.model.models.skill.SkillModelElement;

public class SkillsBinder implements Binder<SkillModelElement>, Serializable {
	/**
	 * Autogenerated by eclipse.
	 */
	private static final long serialVersionUID = -8682660437814936698L;
	
	public static final SkillsBinder ONLY = new SkillsBinder();
	
	private SkillsBinder() {
		
	}
	
	@Override
	public int getView() {
		return SubtextBinder.ONLY.getView();
	}

	@Override
	public void bind(View view, SkillModelElement model) {
		SubtextBinder.ONLY.bind(view, model);

		TextView text = (TextView) view.findViewById(R.id.list_item_text);
		if(model.getIsDisabled()) {
			text.setTextColor(Color.LTGRAY);
		} else {
			text.setTextColor(Color.BLACK);
		}
	}

}
