package com.github.kolandroid.kol.android.binders;

import android.view.View;

import com.github.kolandroid.kol.android.R;
import com.github.kolandroid.kol.model.elements.interfaces.ModelGroup;

public class ColoredGroupBinder extends DefaultGroupBinder {
    /**
     * Autogenerated by eclipse.
     */
    private static final long serialVersionUID = -3546375353421L;

    private final int backColor;

    public ColoredGroupBinder(int backColor) {
        this.backColor = backColor;
    }

    @Override
    public void bind(View view, ModelGroup<?> model) {
        super.bind(view, model);

        View v = view.findViewById(R.id.list_header_background);
        //noinspection deprecation
        v.setBackgroundColor(view.getResources().getColor(backColor));
    }
}
